import React from 'react';
import {Card, Col, Container, Form, Image, Row} from "react-bootstrap";
import bisStar from '../assets/bigStar.png'

const DevicePage = () => {
    const device = {id: 1, name: 'Iphone 12 pro', price: 25000, rating: 5, img: `https://www.91-img.com/gallery_images_uploads/2/7/2736acf8e6adc92cf6970c457d2112c6a7a65767.JPG?tr=h-550,w-0,c-at_max`}
    return (
        <Container className="mt-3">
            <Row>
                <Col md={4}>
                    <Image width={300} height={300} src={device.img}/>
                </Col>
                <Col md={4}>
                    <Form className="d-flex flex-column align-items-center">
                        <h2>{device.name}</h2>
                        <div
                            className="d-flex align-items-center justify-content-center"
                            style={{background: `url(${bisStar}) no-repeat center center`, width: 240, height: 240, backgroundSize: 'cover', fontSize: 64}}
                        >
                            {device.rating}
                        </div>
                    </Form>
                </Col>
                <Col md={4}>
                    <Card>
                        <h3>{device.price}</h3>
                        <Button>Добавить в корзину</Button>
                    </Card>
                </Col>
            </Row>
        </Container>
    );
};

export default DevicePage;
